var indices = funcs.multiselect_selection("rows");
if (!indices) {
  funcs.msg(funcs.scmsg('choose.training.to.view', 'SmartTicket'), 3);
  return;
}

var _ = lib.Underscore.require(),
    IDOLSmartTicketCategory = lib.IDOLSmartTicketCategory.require();

var fCategory = vars['$L.file'];
var ticketIds = vars['$training.ticket.id'];
var statuses = vars['$training.ticket.status'];

var refs = _.map(indices, function(i) {
  var status = statuses[i - 1], ref = ticketIds[i - 1];
  if (status === 'normal') {
    return ticketIds[i - 1];
  } else {
    funcs.msg(funcs.scmsg('not.training.document', 'SmartTicket', [ ref ]), 2);
    return null;
  }
});

refs = _.compact(refs);

var category = IDOLSmartTicketCategory.wrap(fCategory);
var client = category.createClient();

_.each(refs, function(ref) {
  var relevance = category.evaluateTraining(client, ref);
  if (relevance) {
    funcs.msg(funcs.scmsg('show.relevance', 'SmartTicket', [ ref, fCategory.id, relevance]));
  } else {
    funcs.msg(funcs.scmsg('no.relevance', 'SmartTicket', [ ref, fCategory.id]), 2);
  }
});
