record.javascript.pre:
var changetTask = vars.$L_file;
var fileName = vars.$fileName;
var rets = lib.SMOOAutomationTaskService.executeOOFlow(changetTask, fileName);
changetTask.ooexecution_runid = lib.c.$.createSCArray();
changetTask.ooexecution_reporturl = lib.c.$.createSCArray();
changetTask.ooexecution_status_list = lib.c.$.createSCArray();

if (rets) {
	var isArrauRet = rets instanceof Array;
	if (isArrauRet) {
		for (var i = 0; i < rets.length; i++) {
			createUpdateInfo(rets[i]);
		}
	}
	changetTask.ooexecution_status="In Progress";
	changetTask.doSave();
}

/*
 * ret[0] is reporturl;
 * ret[1] is returnCode;
 * ret[2] is activityLogID;
 * ret[3] is flowresult;
 * ret[4] is runid;
 * ret[5] is async;
 */
function createUpdateInfo(ret) {
	changetTask.ooexecution_runid.push(ret[4]);
	changetTask.ooexecution_reporturl.push(ret[0]);
	if (!ret[5] && ret[3] == "success" && lib.SMOOAutomationTaskService.isResolvedCode(ret[1])) {
		changetTask.ooexecution_status_list.push("Success");
	} else if (!ret[5] && ret[3] != "success" && lib.SMOOAutomationTaskService.isErrorCode(ret[1])) {
		changetTask.ooexecution_status_list.push("Error");
	} else if (ret[5]) {
		changetTask.ooexecution_status_list.push("In Progress");
		var toBeUpdate = new SCFile("AutomationTaskUpdate");
		toBeUpdate.fileName = fileName;
		toBeUpdate.fileId = changetTask.number;
		toBeUpdate.ooflow_runid = ret[4];
		toBeUpdate.lastUpdateTime = new Date();
		toBeUpdate.doSave();
	}
}
