record.javascript.pre:
//vars.$result = lib.SRCConfigWizard.updateSrcConfig(vars.$name, vars.$screen_type, vars.$company);
var file = new SCFile('gwPortalFormConfig');
if (RC_SUCCESS == file.doSelect('name="' + vars['$L.file'].name + '"')) {
	if (vars.$name != vars['$L.file'].name) {
		if (RC_SUCCESS == new SCFile('gwPortalFormConfig').doSelect('name="' + vars.$name + '"')) {
			print(system.functions.scmsg(128, 'sprfwizard'));
			vars.$result = false;
			return;
		}
	}

	if (
		RC_SUCCESS == 
			new SCFile('gwPortalFormConfig').doSelect(
				'screen.type="' + vars['$screen.type'] + 
				'" and company="' + vars.$company + 
				'" and name~="' + vars['$L.file'].name + '"')
		) {
		print(system.functions.scmsg(129, 'sprfwizard'));
		vars.$result = false;
		return;
	}

    file.module = vars.$module;
	file.name = vars.$name;
	file.company = vars.$company;
	file['screen.type'] = vars['$screen.type']
	file['enable.upload'] = vars['$enable.upload'] 
	vars.$result = file.doUpdate() == RC_SUCCESS;
} else {
	vars.$result = false;
}
