record.javascript.post:
if(vars["$redo.begin.date"]!=null && vars["$L.wizard.exit"]=="normal"){

  var objFile = new SCFile("idolpbmhunter", SCFILE_READONLY);
  var sql = "true";
  var totalCountSum = 0;

  if (objFile.doSelect(sql) == RC_SUCCESS) {
    do {      
      if(lib.aciindex.redoEnable(objFile["pbmhunter.id"])){
      	var totalCount = lib.IDOL_DisasterRecovery.getDeltaReindexTotalCount(objFile["table.name"], system.functions.val(vars["$redo.begin.date"], 3));
      	totalCountSum = totalCountSum + totalCount;
        var indexRecord = lib.aciindex.getIndexRecord(objFile["table.name"]);
	    indexRecord["last.index.time"] = system.functions.val(vars["$redo.begin.date"], 3);
	    var rc = indexRecord.doUpdate(); 
	  }

    } while (objFile.getNext() === RC_SUCCESS);
  }

  if(totalCountSum > 0){
    print(system.functions.scmsg("hta_redo_success","idol_disaster_recovery", [vars["$redo.begin.date"]]));
  } else {
    print(system.functions.scmsg("no_need_waiting","idol_disaster_recovery"));
  }
}

system.functions.cleanup(vars["$iscancel"]);
