record.javascript.post:
var logger = getLog('Process:idolrestore.addjob');

if(vars["$L.wiz.exit"] != null && vars["$L.wiz.exit"] != "cancel"){

    var callRtn = new SCDatum;
    var nextNumber = new SCDatum;
    system.functions.rtecall("getnumber", callRtn, nextNumber, "idolrestorejob");
    vars["$L.file"].initialID = nextNumber.getText();

	var newjob =   vars["$L.file"];
	var rc = newjob.doSave();

	// http://16.187.188.150:9000/action=RestoreServer&filename=c:\BackupFolderName\***.zip
	// http://<CE1 host>:<CE1 index port>/DREINITIAL?Path=<CE1 backup folder path>

	if(rc == RC_SUCCESS){	   
	   	var endPoint = newjob["endpoint"];
		var url = endPoint.substring(0, endPoint.length - 1);
		var indexPort = null;
		var aciUrl = url + ":" + newjob["port"] + "/";

		if(newjob["type"]=="Restore Content"){
		   indexPort = lib.IDOL_Restore.getIndexPort( aciUrl );
		}else{		   
		   indexPort = newjob["port"];
		}

		if(indexPort!=null){
			url = url + ":" + indexPort + "/";

			if(newjob["type"]=="Restore Content"){
				url = url + "DREINITIAL?InitialID="+newjob["initialID"]+"&"+"Path=" + newjob["restore.filepath"];
			}else{
				url = url + "action=RestoreServer&actionid="+ newjob["initialID"] +"&filename=" + newjob["restore.filepath"];
			}

			try{

			   var result = lib.jsaci.sendRequestToIDOL(url, "")
			   newjob["starttime"] = system.functions.tod();
			   if(result != null){
			       newjob["restore.status"] = 1;
				   newjob.doUpdate();
				   lib.IDOL_Restore.setNeedSyncCollectionFile(aciUrl);
				   print(system.functions.scmsg("create_restore_job_succeed","idol_restore"));
			   }else{
			       newjob["restore.status"] = 3;
			       newjob.doUpdate();
			       print(system.functions.scmsg("create_restore_job_error_3","idol_restore"));
			   }
			}
			catch(e)
			{
			   print(system.functions.scmsg("create_restore_job_error_3","idol_restore"));
			   newjob["starttime"] = system.functions.tod();
			   newjob["restore.status"] = 3;
			   newjob.doUpdate();	

			   logger.error(e);	   
			}
		}else{
			print(system.functions.scmsg("create_restore_job_error_1","idol_restore"));
			newjob["starttime"] = system.functions.tod();
			newjob["restore.status"] = 3;
			newjob.doUpdate();				
		}
	}else{
		print(system.functions.scmsg("create_restore_job_error_2","idol_restore"));
	}
}
