record.javascript.post:
var restorejob = new SCFile('idolrestorejob');
var jobNumber = restorejob.doCount("true");


var index = system.functions.cursor_line(1);


if (index > jobNumber || index < 1) {
   print(system.functions.scmsg("create_restore_job_error_4","idol_restore"));
}else{
   var actionId = vars["$restore.jobId"][index-1];
   var query="initialID=\""+ actionId +"\"";

   var rc = restorejob.doSelect(query);
   if(rc == RC_SUCCESS){
      if(restorejob["restore.status"]=="1"){
	      var endPoint = restorejob["endpoint"];
		  var url = endPoint.substring(0, endPoint.length - 1);
		  url = url + ":" + restorejob["port"] + "/";

		  if(restorejob["type"]=="Restore Content"){
			url = url + "action=indexergetstatus&IndexCmd=DREINITIAL"
			restorejob["restore.status"] = lib.IDOL_Restore.getIndexStatus(url, actionId);
		  }else{
			var idolAction = "action=threadStatus";
			restorejob["restore.status"] = lib.IDOL_Restore.getThreadStatus(url, idolAction, actionId);
		  }

		  restorejob.doUpdate();	  
      }else{
         //print("no need refresh");
      }
   }
}
