if (!vars['$new.okm.url']) {
	funcs.scmsg('99','idol');
} else {
	if (!lib.IDOL_Utilities.isValidIdolURL(vars['$new.okm.url'])) {
		system.functions.msg(system.functions.scmsg('idol_47','fc'), 3);
		return;
	}
	if(lib.IDOL_SharePoint.require().existUrl(vars['$new.okm.url'], 'okm')){
		system.functions.msg(system.functions.scmsg('idol_41','fc'), 3);
	    return;
	}
	var url = new SCDatum();
	url.setType(9);
	url.push(vars['$new.okm.url']);
	url.push('okm');

	vars['$L.file']['other.urls'].push(url);

	var status = lib.jsaci.checkServerStatus(vars['$new.okm.url'], vars['$L_file']['use_oem_license']);

	if (status != true) {
    	vars['$okm.status'].push(funcs.scmsg('77', 'idol')); // offline
    } else {
    	vars['$okm.status'].push(funcs.scmsg('76', 'idol')); // online
    }

    vars['$okm.url'] = funcs.denull(vars['$okm.url']);
    vars['$okm.url'].push(vars['$new.okm.url']);

	vars['$new.okm.url'] = '';
}
