record.javascript.pre:
// Error check for empty phrases.

vars.$oktosave = true;
vars.$validnumber = true;

var arrayLength = vars.$L_file.phrasecount.length();
var m; 
for (m=0; m<=arrayLength-1; ++m)
{
	var phrasecount = vars.$L_file.phrasecount[m];

	if ( !system.functions._null(phrasecount) && 
	     ((phrasecount[0] == null || phrasecount[0] == "" ) && phrasecount[1] != null) )
	{
		vars.$oktosave = false;
		print(system.functions.str(phrasecount));
		break;
	}

	if (!system.functions._null(phrasecount) && isNaN(phrasecount[1]))
	{
		vars.$validnumber = false;
		break;
	}

}

record.javascript.post:
if (vars.$oktosave && vars.$validnumber)
{
	rc = vars.$L_file.doSave();
	print(system.functions.scmsg(194, "km")); // Adaptive learning record saved.
}	
else
	if (vars.$validnumber)
	{
		print(system.functions.scmsg(250, "km")); // "Error: You must add a phrase for the count."
	}
	else
		print(system.functions.scmsg(248, "km")); // "Error: Please enter a valid numeric value."
