record.javascript.pre:
vars.$L_oktocontinue = true;
vars.$L_serverchanged = false;

lib.KMCollection_Create.addTriggers();


if (vars['$lo.idol.enabled']) {
  return;
}

// record is same as $L.file in RAD
var record = vars.$L_file;
if (record.kbtype != "sclib") {
  record.mastermimelist = "";
  var webLib = new SCFile("kmknowledgebase");
  var webFind = webLib.doSelect("kbname = \"" + record.kbname + "\"");
  var result = 0;
  if (lib.KMCollection_WebCrawl.didJobChange(webLib, record)) {
    result = lib.KMCollection_WebCrawl.deleteJob(webLib);
  }
  result = lib.KMCollection_WebCrawl.createJob(record);
  // TODO: use table to store error message id;
  if (result < -1) {
    var errormsg = "";
    if (lib.KMSearchInterface.isJobHTTPException(result) || lib.KMSearchInterface.isJobIOException(result)) {
      errormsg = funcs.scmsg(230, "km");
    }
    if (lib.KMSearchInterface.isJobNullPointerException(result)) {
      errormsg = funcs.scmsg(231, "km");
    }
    if (lib.KMSearchInterface.isJobNotFound(result)) {
      errormsg = funcs.scmsg(226, "km");
    }
    if (lib.KMSearchInterface.isSchedulerException(result)) {
      errormsg = funcs.scmsg(227, "km");
    }
    if (lib.KMSearchInterface.isJobNameInvalid(result)) {
      errormsg = funcs.scmsg(228, "km");
    }
    if (lib.KMSearchInterface.isJobStopException(result)) {
      errormsg = funcs.scmsg(229, "km");
    }
    lib.KMUtils.domessageBox(errormsg);
  }
}

// check that servername did not change
var kbLib = new SCFile("kmknowledgebase");
var kbFind = kbLib.doSelect("kbname = \"" + record.kbname + "\"");
if (kbLib.searchservername != record.searchservername) {
  vars.$L_message = funcs.scmsg(249, "km");
  vars.$L_serverchanged = true;
  vars.$L_oldsearchserver = kbLib.searchservername;
}

record.javascript.post:
var record = vars['$L.file'];
if (vars['lo.idol.enabled']) {
  vars.$L_action = record.sysmodtime ? "save" : "add";
} else {
  if (vars.$L_serverchanged) {
    if (vars.$L_response == "yes") {
      // delete collection here from old search server
      print("delete collection here: " + vars.$L_oldsearchserver);
      lib.KMCollection_Remove.removeCollection(record);
      vars.$L_action = "save";
    } else {
      vars.$L_action = "display";
    }
  } else {
    vars.$L_action = "save";
  }
}
