record.javascript.pre:
print('kmknowledgebase.reindex_library');
var index = funcs.cursor_line(1);
var kbname = vars['$kbname'][index - 1];
var query = 'kbname = "' + kbname + '"';
var kmknowledgebase = new SCFile('kmknowledgebase');
var rc = kmknowledgebase.doSelect(query);
if (rc === RC_SUCCESS) {
	var tmp = vars['$L.file'];
	vars['$L.file'] = kmknowledgebase;
	result = lib.KMCollection_Update_IDOL.require().reindex(kmknowledgebase);

    kmknowledgebase = vars['$L.file'];
    vars['$L.file'] = tmp;

    if (kmknowledgebase.kbtype === 'sclib'){
    	if (kmknowledgebase.indexstatus == lib.KMCollection_Status_Constant.INDEXSTATUS_OFFLINE()){
    	      kmknowledgebase.indexstatus = lib.KMCollection_Status_Constant.INDEXSTATUS_NOTSTARTED();
    	}
    }else{
    	    kmknowledgebase.indexstatus = lib.KMCollection_Status_Constant.INDEXSTATUS_INDEXING();
    }
    kmknowledgebase.doUpdate();

    lib.KMknowledgebase.showListInUI();    
    //vars['$L.action'] = 'refresh';
}
