vars.$L_message_url = null;

if (lib.MicrosoftTeamsConfiguration.isEnabled()) {
    // generate teams channel message
    var messageInfo = lib.MicrosoftTeamsChannel.generateChannelMessage(vars.$L_file, "default");

    var error = messageInfo.error;
    if (error) {
        var msg;
        // show friendly error message for certain error codes
        if (error.code == lib.MicrosoftTeamsConstants.ERROR_CODE_NOTFOUND() ||
            error.code == lib.MicrosoftTeamsConstants.ERROR_CODE_FORBIDDEN()) {
            msg = error.message;
        } else {
            msg = lib.MicrosoftTeamsUtil.errorMessage(error);
        }
        funcs.rtecall("msg", new SCDatum(), msg, 3);
    } else {
        vars.$L_message_url = messageInfo.webUrl;
    }
} else {
    funcs.rtecall("msg", new SCDatum(), funcs.scmsg("ConfigurationManager_8", "smis"), 3);
}
