record.javascript.pre:
var $ = lib.c.$;
var msg = system.functions.scmsg;

vars["$L.preview"] = true;
if (vars["$L.surveyID"]==null || vars["$L.surveyID"]=="")
{
	vars["$L.preview"]=false;
	vars["$L.error.msg"] = msg(38, "survey");
}

var devtype = new SCFile("devtype")
var rc = devtype.doSelect("joindef=\""+vars["$L.fname"]+"\"");
if (rc==RC_SUCCESS)
{
	vars["$L.fname"] = "device";
}

//check survey template
if (vars["$L.preview"] && vars["$L.surveyID"]!=null && vars["$L.surveyID"]!="")
{
	var template = $('SurveyTemplate', SCFILE_READONLY).select('id="'+vars["$L.surveyID"]+'" and current.phase="published" and (module="'+vars["$L.fname"]+'" or module=NULL)').uniqueResult();
	var flag = false;
	if (template!=null)
	{	
		if (system.functions.filename(vars["$L.file"])!="SurveyDefinition")
		{			
			flag = lib.SurveyTemplateHelpers.checkSurvey(template);
		}
		else
		{
			flag = true;
		}
	}
	if (!flag)
	{
		vars["$L.preview"]=false;
		vars["$L.error.msg"] = msg(41, "survey");
	}	
}

//check reference ticket number
if (vars["$L.preview"] && (system.functions.filename(vars["$L.file"])=="SurveyDefinition" && vars["$L.ticketID"]!=null) )
{
	var sql2 = vars["$L.uniKey"]+"=\""+vars["$L.ticketID"]+"\"";
	var ticketRec = $(vars["$L.fname"],SCFILE_READONLY).select(sql2).uniqueResult();
	if(ticketRec==null)
	{
		vars["$L.preview"]=false;
		var idx = system.functions.index(vars["$L.fname"], vars["$G.survey.objects.list"]);
		var displayTableName = vars["$G.survey.objects.disp"][idx-1];		
		vars["$L.error.msg"] = msg(141, "pm", [sql2]);
	}
}
