record.javascript.pre:
var $ = lib.c.$;

$("contentversion", SCFILE_READONLY).setFields(["module","deprecated"]).select("true").iterate(function(content) {

	// ignore deprecated content pack
	if (content.deprecated) {
		return false;
	}

	var module = content.module;

	// append version information
	var info = $("patchrelversioninfo", SCFILE_READONLY).setFields(["version"]).setOrderBy(["version"],[SCFILE_DSC]).select("module=\"" + module + "\"").uniqueResult();
	if (info != null) {
		module += "  " + info.version;	
	}

	vars.$L_contentversion = system.functions.insert(vars.$L_contentversion, 0, 1, module);	
});
