var fm = new SCFile("SMISFieldMapping");
funcs.fduplicate(fm, vars["$L.file"]);

var variables = lib.smis_FieldMappingManager.getFieldMappingVariables("SMISFieldMapping");

var i, j;
if (vars["$L.currentConfig"]["module.enabled"]) {
    fm.fieldMapping = [];

    var lng = lib.smis_CommonLib.alignVariableArrayLength(variables);
    var lng2 = variables.length;

    var temp;
    var strStruct = "{[";
    for (j=0; j<lng2; j++) {
        strStruct +=",";
    }
    strStruct += "]}";
    var allnull;
    for (i=0; i<lng; i++) {
        allnull = true;
        temp = "$L.temp" + i;
        funcs.parse_evaluate(temp + "=" + strStruct);
        // order is from dbdict SMISFieldMapping
        // order follows the fields of fieldMapping in dbdict SMISFieldMapping
        for (j=0; j<lng2; j++) {
            vars[temp][j] = vars[variables[j]][i];
            if (allnull && vars[variables[j]][i] != null) {
                allnull = false;
            }
        }

        // The line that all values are null is not valid
        if (allnull) {
            continue;
        }

        vars[temp][j] = vars["$smis.selected.module"];
        fm.fieldMapping.push(vars[temp]);
    }
}


vars.$L_validateFMMessage= lib.smis_FieldMappingManager.validateMapping(fm,vars.$L_currentConfig);

if (!vars["$L.viewModel"] && vars["$L.validateFMMessage"] == null && vars["$L.currentConfig"]["module.enabled"]) {
    // Duplicate $L.file but clean fieldmapping
    vars["$L.file.bak"] = new SCFile(funcs.filename(vars["$L.file"]));
    funcs.fduplicate(vars["$L.file.bak"], vars["$L.file"]);
    vars["$L.file"]["fieldMapping"] = [];

    lng = vars["$L.file.bak"]["fieldMapping"] == null ? 0 : vars["$L.file.bak"]["fieldMapping"].length();

    // Copy all fieldmapping with other modules to $L.file
    for (i=0; i<lng; i++) {
        if (vars["$L.file.bak"]["fieldMapping"][i]["SMModule"] != vars["$smis.selected.module"]
              && vars["$L.file.bak"]["fieldMapping"][i]["SMField"] != null) {
            vars["$L.file"]["fieldMapping"].push(vars["$L.file.bak"]["fieldMapping"][i]);
        }
    }

    // Copy fieldmapping with the current module to $L.file
    lng = fm["fieldMapping"] == null ? 0 : fm["fieldMapping"].length();
    for (i=0; i<lng; i++) {
        vars["$L.file"]["fieldMapping"].push(fm["fieldMapping"][i]);
    }
}
