//Populate a Field Mapping instance

var fm = new SCFile("SMISFieldMapping");

var registry = new SCFile("SMISRegistry");

// For SMISFieldMapping order
var variables = lib.smis_FieldMappingManager.getFieldMappingVariables("SMISFieldMapping");

// For SMISRegistry order as it is different from SMISFieldMapping
var variables2 = lib.smis_FieldMappingManager.getFieldMappingVariables("SMISRegistry");

var i, j;
if (vars["$L.file"]["module.enabled"]) {
    registry.fieldMapping = [];
    var lng = lib.smis_CommonLib.alignVariableArrayLength(variables);
    var lng2 = variables.length;

    var temp, tmp;
    var strStruct = "{[";
    for (j=0; j<lng2; j++) {
        strStruct +=",";
    }
    strStruct += "]}";
    var allnull;
    for (i=0; i<lng; i++) {
        allnull = true;
        temp = "$L.temp" + i;
        tmp = "$L.tmp"+i;
        funcs.parse_evaluate(temp + "=" + strStruct);
        funcs.parse_evaluate(tmp + "=" + strStruct);
        // order is from dbdict SMISFieldMapping and SMISRegistry
        for (j=0; j<lng2; j++) {
            // order follows the fields of fieldMapping in dbdict SMISFieldMapping
            vars[temp][j] = vars[variables[j]][i];
            // order follows the fields of fieldMapping in dbdict SMISRegistry
            vars[tmp][j] = vars[variables2[j]][i];
            if (allnull && (vars[variables[j]][i] != null || vars[variables2[j]][i] != null)) {
                allnull = false;
            }
        }

        // The line that all values are null is not valid
        if (allnull) {
            continue;
        }

        vars[temp][j] = vars["$smis.selected.module"];
        vars[tmp][j] = vars["$smis.selected.module"];

        fm.fieldMapping.push(vars[temp]);
        registry.fieldMapping.push(vars[tmp]);
    }
} else {
    for(i in vars.$L_file.fieldMapping) {	
        fm.fieldMapping[i].SMField = vars.$L_file.fieldMapping[i].SMField;
        fm.fieldMapping[i].EPField = vars.$L_file.fieldMapping[i].EPField;
        fm.fieldMapping[i].SMDefValue = vars.$L_file.fieldMapping[i].SMDefValue;
        fm.fieldMapping[i].EPDefValue = vars.$L_file.fieldMapping[i].EPDefValue;
        fm.fieldMapping[i].SMjscallback = vars.$L_file.fieldMapping[i].SMjscallback;
        fm.fieldMapping[i].EPjscallback = vars.$L_file.fieldMapping[i].EPjscallback;
        fm.fieldMapping[i].direction = vars.$L_file.fieldMapping[i].direction;
        fm.fieldMapping[i].FMDescription = vars.$L_file.fieldMapping[i].FMDescription;
        fm.fieldMapping[i].valueMappingGroup = vars.$L_file.fieldMapping[i].valueMappingGroup;
    }
}

for(j in vars.$L_file.valueMapping) {

	fm.valueMapping[j].SMValue = vars.$L_file.valueMapping[j].SMValue;
	fm.valueMapping[j].EPValue = vars.$L_file.valueMapping[j].EPValue;
	fm.valueMapping[j].valueMappingGroupID = vars.$L_file.valueMapping[j].valueMappingGroupID;
	fm.valueMapping[j].VMDescription = vars.$L_file.valueMapping[j].VMDescription;
}

//this vars.$mockSMISConfig is from the wizard named "SMIS Registry Field Mapping"

vars.$L_validateRFMMsg= lib.smis_FieldMappingManager.validateMapping(fm,vars.$L_mockSMISConfig);

if (vars["$L.validateRFMMsg"] == null && vars["$L.file"]["module.enabled"]) {
    // Duplicate $L.file but clean fieldmapping
    vars["$L.file.bak"] = new SCFile(funcs.filename(vars["$L.file"]));
    funcs.fduplicate(vars["$L.file.bak"], vars["$L.file"]);
    vars["$L.file"]["fieldMapping"] = [];

    var lng = vars["$L.file.bak"]["fieldMapping"] == null ? 0 : vars["$L.file.bak"]["fieldMapping"].length();
    // Copy all fieldmapping with other modules to $L.file
    for (i=0; i<lng; i++) {
        if (vars["$L.file.bak"]["fieldMapping"][i]["SMModule"] != vars["$smis.selected.module"]
              && vars["$L.file.bak"]["fieldMapping"][i]["SMField"] != null) {
            vars["$L.file"]["fieldMapping"].push(vars["$L.file.bak"]["fieldMapping"][i]);
        }
    }

    // Copy fieldmapping with the current module to $L.file
    lng = registry["fieldMapping"] == null ? 0 : registry["fieldMapping"].length();
    for (i=0; i<lng; i++) {
        vars["$L.file"]["fieldMapping"].push(registry["fieldMapping"][i]);
    }
}
