record.mappingScript:
if (context.inbound) {
    vars["$apm.activity"] = "Update from customer";
    if (context.action=="add") {
        //set correct assignment group for the internal team
       mapObj["assignment"]="Application";   
       if (ep["Category"]=="request for information") {
            mapObj["category"]="request for information";
            mapObj["subcategory"]="how to";  
            mapObj["product.type"]="how to";  
        } else {
            mapObj["category"]="incident";
            mapObj["subcategory"]="data";  
            mapObj["product.type"]="data or file incorrect";  
        }
    }

    //cleanup vendor when set and status is no longer pending vendor
    if (context.internalObject["problem.status"]=="Pending Vendor" && sm["problem.status"]=="Work In Progress") {
        mapObj["vendor"]=null;
    }

    // if resolved but no closure code was provided, set default closure code
    if (!ep["ClosureCode"] &&  (ep["Status"]=="Resolved" || ep["Status"]=="Closed")) {
        mapObj["resolution.code"]="Solved by Workaround";
    }

    vars["$pmc.actions"] = ep["JournalUpdates"];

    if (!vars["$pmc.actions"] || !vars["$pmc.actions"][0]) {
        // if rejected by EP but no activity was created, set default activity 
        if (context.rejectedByEP) {
            vars["$pmc.actions"]=["Record rejected by endpoint"];
        }
        // if null set default update
        else {
            vars["$pmc.actions"]=["External Update"];
        }
    }
}

record.preScript:
//check whether the SM Incident module applied Process Designer or not
context.NoPDtoNoPD=false;
context.PDtoPD=false;
context.NoPDtoPD=false;
context.PDtoNoPD=false;
context.PDtoPD=false;
context.isPDIncident_SM = false;
context.isPDIncident_EP = false;
  context.rejectedBySM = false;
  context.rejectedByEP = false;
if (lib.ProcessDesignerEnablement.isIncidentEnabled()) context.isPDIncident_SM = true;
if (param["pdObjectInEP"]) context.isPDIncident_EP = true;
if (!context.isPDIncident_SM && context.isPDIncident_EP) context.NoPDtoPD=true;
if (context.isPDIncident_SM && !context.isPDIncident_EP) context.PDtoNoPD=true;
if (context.isPDIncident_SM && context.isPDIncident_EP) context.PDtoPD=true;
if (!context.isPDIncident_SM && !context.isPDIncident_EP) context.NoPDtoNoPD=true;

//get the task direction
context.inbound = false;
context.outbound = false;
if (lib.CaseExchange_CommonLib.isInbound(context.direction)) context.inbound = true;
if (lib.CaseExchange_CommonLib.isOutbound(context.direction)) context.outbound = true;

//get local SM ticket status
context.SMStatus = "";

if (context.inbound) {
    //get whether the ticket has already been exchanged to SM
    context.linked2SM = true;
    if (context.internalId == null) context.linked2SM = false;
    if (context.linked2SM) context.SMStatus=context.internalObject['problem.status'];

  // check the endpoint status to see if a ticket needs to be re-opened -> a new ticket is created in case the record is closed 
    if ((ep["Status"] == "Pending Customer" || ep["Status"] == "Pending Vendor") && context.SMStatus == "Closed" ) context.action = "add";

    // check the endpoint status to see if a ticket needs to be re-opened -> a ticket is reopened in case the record is resolved in a Non PD system
    if ((ep["Status"] == "Pending Customer" || ep["Status"] == "Pending Vendor") && !context.isPDIncident_SM && context.SMStatus == "Resolved") context.action = "reopensave";

  // check if the ticket was rejected
  if (ep["Status"] == "Rejected"  || ((ep["Status"] == "Resolved" || ep["Status"] == "Closed") && ep["ClosureCode"]=="Request Rejected")) {
     context.rejectedByEP=true; 
     context.action = "save";
  }
  // check if ticket needs to be resolved or closed-> In case the ticket in NON PD System is "resolved" or in case  the ticket in a PD system is closed.
  if (context.rejectedByEP==false && (ep["Status"] == "Resolved" || ep["Status"] == "Closed")) {
     if(!context.isPDIncident_SM  && context.linked2SM && context.SMStatus != "Closed" && context.SMStatus != "Resolved") context.action = "resolvebg";
     if(context.isPDIncident_SM  && context.linked2SM && context.SMStatus != "Closed" && context.SMStatus != "Resolved") context.action = "save";
  }
}

if (context.outbound) {
    //get whether the ticket has already been exchanged to Endpoint
    context.linked2EP = true;
    if(context.externalId == null) context.linked2EP = false;

    if(context.SMStatusOld!=context.SMStatus) {
       if(context.action=="Reject" && context.internalObject['resolution.code']=="Request Rejected") context.rejectedBySM = true;
   }
    //get SM ticket old status
    context.SMStatus = sm['problem.status'];
    context.SMStatusOld = sm['oldvalues']['problem.status'];
    context.RESTACTION="Update";
    // send the correct resolve status for PD/NON PD Systems
    if (context.action=="Resolve" && !context.isPDIncident_EP  && context.linked2EP && !context.rejectedBySM) context.RESTACTION = "Resolve";
}
