record.mappingScript:
if(context.inbound) {
    //set the default value of required Master Data when create the ticket
    if(context.action=="add") {
        mapObj["category"]="incident";   //set the default category
        mapObj["subcategory"]="failure";   //set the default area
        mapObj["product.type"]="job failed";   //set the default subarea
        mapObj["assignment"]="Application";   //set the default assignment group
        mapObj["affected.item"]=lib.DisplayName.convertNameToId("Applications","","");//set the default affected service
    }
}

if (context.outbound) {
    context.action = mapObj["ext_properties.Operation"];
    //set the default value of required Master Data when create the ticket
    if(context.action=="Create") {
        mapObj["properties.RegisteredForActualService"]="10019";   //set the default service
    }

    //Transform the single activity record to an array which contain some objects
    mapObj['Comments']=[];
    if (sm['$pmc.actions'] != null && sm['$pmc.actions'] !="") {
        mapObj['Comments'][0]={};
        mapObj['Comments'][0]['Body']=sm['$pmc.actions'];
        mapObj['Comments'][0]['CreatedTime']=(new Date()).getTime();
    }
}

record.preScript:
//check whether the Incident module applied Process Designer or not
context.isPDIncident = false;
if(lib.ProcessDesignerEnablement.isIncidentEnabled()) context.isPDIncident = true;

//get the task direction
context.inbound = false;
if(lib.CaseExchange_CommonLib.isInbound(context.direction)) context.inbound = true;
context.outbound = false;
if(lib.CaseExchange_CommonLib.isOutbound(context.direction)) context.outbound = true;

//get SM ticket status
context.SMStatus = "";

if(context.inbound) {
    //get whether the ticket has already been exchanged to SM or not
    context.linked2sm = true;
    if(context.internalId == null) context.linked2sm = false;

    if(context.linked2sm) context.SMStatus=context.internalObject['problem.status'];

    //remove html tags from SAW
    if(ep['properties.Description']) ep['properties.Description'] = lib.CaseExchange_SAWUtil.processHtmlText(param, context, 'description', ep['properties.Description']);
    if(ep['properties.Solution']) ep['properties.Solution'] = lib.CaseExchange_SAWUtil.processHtmlText(param, context, 'solution', ep['properties.Solution']);
    if (ep['Comments'] && ep['Comments'].length > 0) {
        for (var i = 0; i < ep['Comments'].length; i++) {
            ep['Comments'][i]['Body'] = lib.CaseExchange_SAWUtil.processHtmlText(param, context, 'comment', ep['Comments'][i]['Body']);
        }
    }

    //map SM action to the incoming SAW operation
    if(ep["ext_properties.Operation"] == "Reopen") {
        if(!context.linked2sm || context.SMStatus == "Closed") context.action = "add";
        if(!context.isPDIncident && context.SMStatus == "Resolved") {
            //ignore the empty solution and closure code updated from SAW, since they are required while call the SM action "reopensave"
            ep["properties.Solution"] = null;
            ep["properties.CompletionCode"] = null;
            context.action = "reopensave";
        }
    }
    if(ep["ext_properties.Operation"] == "Resolve" || ep["ext_properties.Operation"] == "Cancel" || ep["ext_properties.Operation"] == "Close") {
        if(!context.isPDIncident && context.linked2sm && context.SMStatus != "Closed" && context.SMStatus != "Resolved") context.action = "resolvebg";
    }
}

if(context.outbound) {
    //get whether the ticket has already been exchanged to SAW or not
    context.linked2saw = true;
    if(context.externalId == null) context.linked2saw = false;

    context.SMStatus = sm['problem.status'];
    //get SM ticket old status
    context.SMStatusOld = sm['oldvalues']['problem.status'];

    //define the condition used to further map with SAW operation
    context.cancelledBySM = false;
    context.rejectedBySM = false;
    context.solvedBySM = false;
    if(context.SMStatusOld!=context.SMStatus) {
        if(context.action=="Cancel" && context.internalObject['resolution.code']=="Withdrawn by User") context.cancelledBySM = true;
        if(context.action=="Reject" && context.internalObject['resolution.code']=="Request Rejected") context.rejectedBySM = true;
        if(context.action=="Resolve" && /^Solved by/.test(context.internalObject['resolution.code'])) context.solvedBySM = true;
    }

    //process the value which will be posted to SAW rich text field 
    if(sm['action'] && sm['action'] !== "") {
        if(sm['oldvalues']['action']) sm['action'] = lib.CaseExchange_SAWUtil.processOutboundText(context, 'description', sm['action']);
        if(sm['oldvalues']['action'] === undefined) sm['action'] = null;
    }
    if(sm['resolution'] && sm['resolution'] !== "") {
        if(sm['oldvalues']['resolution']) sm['resolution'] = lib.CaseExchange_SAWUtil.processOutboundText(context, 'solution', sm['resolution']);
        if(sm['oldvalues']['resolution'] === undefined) sm['resolution'] = null;
    }
}
