record.mappingScript:
if (context.action == 'add') {
	// set default company
	if (context['contact.info']) {
		mapObj['company'] = context['contact.info']['company'];
	}
} else {
	var user = "";
	if (context['contact.info']) {
		user = context['contact.info']['name'];
	}
	var lang = lib.NotificationRecipientsHelp.getLang(user);
    var localscmsg = lib.NotificationRecipientsHelp.determinMsg;
    var closureComments = localscmsg(lang,"SmartEmail","closure_comments");
    var resolution = localscmsg(lang,"SmartEmail","default_resolution");
	var category = context.internalObject['category'];

	var file = context['token'].file
	var tokenAction = context['token'].action;

	var isMigrated = lib.ProcessDesignerEnablement.isMigratedWorkflowUsed(file, category);
	var isStreamlined = lib.sdUtil.isStreamlinedSDEnabled();

	var defaultResolution = [resolution];
	if (ep['body'] && ep['body'].length > 0) {
		defaultResolution = ep['body'];
	}

	// configure reject status and accept action
	var ACTION_MAP = {
		'incidents': {
			accept: {action: 'closeme', update: {'resolution.code': 'Fulfilled'}},
			reject: {action: 'save', update: {'open': 'Dispatched'}},
			comment: {action: 'save', update: {}},
			resolve: {action: 'save', update: {'open': 'Resolved', 'resolution': defaultResolution}}
			},
		'probsummary': {
			accept: {action: 'close', update: {'resolution.code': 'Resolved Successfully'}},
			reject: {action: 'save', update: {'problem.status': 'Work In Progress'}},
			comment: {action: 'save', update: {}},
			resolve: {action: 'save', update: {'problem.status': 'Resolved', 'resolution': defaultResolution}}
			},
		'rootcause': {
			accept: {action: 'closeme', update: {'closure.code': 'Successful'}},
			reject: {action: 'save', update: {'rcStatus': 'Work In Progress'}},
			comment: {action: 'save', update: {}},
			resolve: {action: '_wfE:resolved.by.interaction', update: {'rcStatus': 'Resolved', 'resolution': defaultResolution}}
			},
		'request': {
			accept: {action: 'Close', update: {'closure.code': '1', 'closure.comments': closureComments}},
			reject: {action: 'Back to Fulfill', update: {}},
			comment: {action: 'save', update: {}},
			resolve: {action: 'save', update: {'status': 'Fulfilled', 'resolution': defaultResolution}}
			},
		'cm3t': {
			close: {action: 'Close', update: {'completion.code': 1, 'closing.comments': closureComments}}
			}
	};

	if (tokenAction == 'comment') {
		if (file == 'probsummary' && context.internalObject['problem.status'] == 'Pending Customer') {
			ACTION_MAP['probsummary']['comment']['update'] = {'problem.status': 'Work In Progress'};
		} else if (file == 'rootcause' && context.internalObject['rcStatus'] == 'Pending') {
			ACTION_MAP['rootcause']['comment']['update'] = {'rcStatus': 'Work In Progress'};
		} else if (file == 'request' && context.internalObject['status'] == 'Pending Customer') {
			ACTION_MAP['request']['comment']['update'] = {'status': 'In Progress'};
		}
	}

	var ACTION_MAP_STREAMLINED = {
		'incidents': {
			accept: {action: 'save', update: {'resolution.code': 'Fulfilled', 'open':'Closed'}},
			comment: {action: 'save', update: {}}
			}
	};

	// special status change for streamlined interaction if status is pending customer 
	if (!isMigrated && isStreamlined && tokenAction == 'comment') {
		if (file == 'incidents' && context.internalObject['open'] == 'Pending Customer') {
			ACTION_MAP_STREAMLINED['incidents']['comment']['update'] = {'open': 'Dispatched'};
		}
	}

	var ACTION_MAP_MIGRATED = {
		'probsummary': {
			reject: {action: 'reopen', update: {'open': 'Open'}},
			resolve: {action: 'resolve', update: {'problem.status': 'Resolved', 'resolution': defaultResolution, 'resolution.code': 'Fulfilled'}}
			}
	};

    var actionMap = null;
    if (ACTION_MAP[file]) {
        actionMap = ACTION_MAP[file][tokenAction];
    }

	if (isMigrated) {
		if (ACTION_MAP_MIGRATED[file] && ACTION_MAP_MIGRATED[file][tokenAction]) {
			actionMap = ACTION_MAP_MIGRATED[file][tokenAction];
		}
	} else {
		if (isStreamlined) {
			if (ACTION_MAP_STREAMLINED[file] && ACTION_MAP_STREAMLINED[file][tokenAction]) {
				actionMap = ACTION_MAP_STREAMLINED[file][tokenAction];
			}
		}
	}


	// set first.call to true if interaction is not escalated
	if (tokenAction == 'resolve') {
		if (file == 'incidents') {
			if (lib.SmartEmailSMHelper.isInteractionEscalated(context.internalObject['incident.id']) == false) {
				actionMap['update']['first.call'] = true;
			}
		}
		// set resolved.by.email to true
		actionMap['update']['resolution.channel'] = 'email';
	}

	context.actionMap = actionMap;
}
