record.script:
// Check all apps records which modified time greater than startDate time.
var params = {
    "startDate" : "09/30/2013 00:00:00", 
    "filePath" : "c://CheckAppsI18NResult.txt"
};

function CheckAppsI18N(params)
{
  var startDate = params["startDate"]; 
  var filePath = params["filePath"];
  print("start Date: " + startDate);
  print("filePath: " + filePath);
  lib.CheckDisplayOptionI18N.CheckAllDisplayOptionsI18N(startDate, filePath);
  lib.CheckFormatI18N.CheckAllFormatsI18N(startDate, filePath);
  lib.CheckFormatCtrlI18N.CheckAllFormatCtrlsI18N(startDate, filePath);
  lib.CheckWorkflowPhaseI18N.CheckAllWorkflowPhasesI18N(startDate, filePath);     
  lib.CheckMenuI18N.CheckAllMenusI18N(startDate, filePath);
  //lib.CheckRuleSetI18N.CheckAllRuleSetsI18N(startDate, filePath);  // From 940, ruleset name doesn't support localization, it is by design to avoid performance issue.
  lib.CheckRuleTypeI18N.CheckAllRuleTypesI18N(startDate, filePath);  
  lib.CheckWorkflowI18N.CheckAllWorkflowsI18N(startDate, filePath);
  lib.CheckLocalizedTableTagFieldI18N.CheckAllLocalizedTableTagFieldsI18N(startDate, filePath);
  lib.CheckWizardI18N.CheckAllWizardsI18N(startDate, filePath);
  lib.CheckGlobalListI18N.CheckAllGlobalListsI18N(startDate, filePath);
}

CheckAppsI18N(params);
