record.script:
//////////////////////////////////////////////////////////////////
//Check DisplayOption I18N Support Start.
//////////////////////////////////////////////////////////////////
var y = 0;
function CheckDisplayOptionI18N(displayoption, filePath)
{
  displayoptionLocalizable(displayoption, filePath);
}

function CheckAllDisplayOptionsI18N(startDate, filePath)
{
  var displayoption = new SCFile("displayoption", SCFILE_READONLY);
  var sql = "sysmodtime>'" + startDate + "'";
  var ret = displayoption.doSelect(sql);
  while (ret == RC_SUCCESS) {
    CheckDisplayOptionI18N(displayoption, filePath);
    ret = displayoption.getNext();
  }  
  print("Total Display Option un-localized list count is: " + y); 
  y = 0;  
}

function displayoptionLocalizable(displayoption, filePath)
{
  var i = 0;
  var id = displayoption.id;
  var defaultLabel = displayoption["default"];
  var balloon = displayoption.balloon;

  if (defaultLabel != null)
  {     
    var scmessage = new SCFile("scmessage", SCFILE_READONLY);   
    defaultLabel = lib.I18NLib.strCovert(defaultLabel);

    var sql = 'syslanguage="en" and class="scbutton" and message.id#"' + lib.StringUtil.escapeAllStr(defaultLabel) + '"';
    var ret = scmessage.doSelect(sql);
    if(ret != RC_SUCCESS)
    {
      var str = "Display Option | " + '"' + displayoption.id + '" button label has no scmessage.';
      print(str);
      writeFile(filePath, "a", str + "\n");
      y++;
    } 
  }  
  if (balloon != null)
  {  
    scmessage = new SCFile("scmessage", SCFILE_READONLY);
    balloon = lib.I18NLib.strCovert(balloon);

    sql = 'syslanguage="en" and class="scballoon" and message.id#"' + lib.StringUtil.escapeAllStr(balloon) + '"';
    ret = scmessage.doSelect(sql);
    if(ret != RC_SUCCESS)
    {
      var str = "Display Option | " + '"' + displayoption.id + '" button balloon help has no scmessage.';
      print(str);
      writeFile(filePath, "a", str + "\n");
      y++;
    } 
  }

}
//////////////////////////////////////////////////////////////////
//Check DisplayOption I18N Support End.
//////////////////////////////////////////////////////////////////



