record.script:
//////////////////////////////////////////////////////////////////
// Check Format I18N Support Start.
//////////////////////////////////////////////////////////////////
var x = 0;
function CheckFormatI18N(format, filePath)
{
  var name = format.name;
  var fields = format.field;
  formatLocalizable(fields, format.name, filePath);
}


function CheckAllFormatsI18N(startDate, filePath)
{
  var format = new SCFile("format", SCFILE_READONLY);
  var sql = "sysmodtime>'" + startDate + "'";
  var ret = format.doSelect(sql);
  while (ret == RC_SUCCESS) {    
    CheckFormatI18N(format, filePath);
    ret = format.getNext();
  }  
  print("Total format un-localized list count is: " + x);   
  x = 0;
}

function formatLocalizable(fields, formatName, filePath)
{
  var i = 0;

  for(var i = 0; i < fields.length(); i++)
  {
    var objectName = fields[i][7];
    var field = fields[i].toString();

    if(field.indexOf("ValueList=$") >= 0)
    {
      if (field.indexOf("DisplayList=$") < 0)
      {
        var str = "Format | \"" + formatName + "\" and its object \"" + objectName + "\" has no display value.";
        print(str);
        writeFile(filePath, "a", str + "\n");
        x++;
      }
    }
  }
  return x;
}
//////////////////////////////////////////////////////////////////
//Check Format I18N Support End.
//////////////////////////////////////////////////////////////////

