record.script:
//////////////////////////////////////////////////////////////////
//Check WorkflowPhase I18N Support Start.
//////////////////////////////////////////////////////////////////
var z = 0;
function CheckWorkflowI18N(workflow, filePath)
{
  workflowLocalizable(workflow, filePath)  
}

function CheckAllWorkflowsI18N(startDate, filePath)
{
  var workflow = new SCFile("Workflow", SCFILE_READONLY);
  var sql = "sysmodtime>'" + startDate + "'";
  var ret = workflow.doSelect(sql);
  while (ret == RC_SUCCESS) {   
    CheckWorkflowI18N(workflow, filePath);
    ret = workflow.getNext();
  }  
  print("Total Workflow un-localized count is: " + z); 
  z = 0;
}

function workflowLocalizable(workflow, filePath)
{

  var actions = workflow.action;
  var workflowName = workflow.name;
  var tableName = workflow.tableName;   
  var scmessage = new SCFile("scmessage", SCFILE_READONLY); 
  // Check Workflow Phase Actions I18N Support.  
  if (actions != null)
  {    
    //print(actions);   
    for (var i = 0; i < actions.length(); i++)
    {      
      var action = actions[i];    
      var messageid = "workflow:" + workflowName + "_" + tableName + ":" + action["actionMsgId"];     
      var actionName = action["actionName"];
      //print(action);
      //print(messageid);      
      var sql = "syslanguage=\"" + "en\"" + " and class=\"" + "wfA\"" + " and message.id=\"" + messageid + "\"";
      var ret = scmessage.doSelect(sql);
      if(ret != RC_SUCCESS && actionName != null)
      {
        var str = "Workflow | \"" + workflowName + "\" action \"" + actionName + "\" has no scmessage.";
        print(str);
        writeFile(filePath, "a", str + "\n");          
        z++;
      } 
    } 
  }    
}
//////////////////////////////////////////////////////////////////
//Check Workflow I18N Support End.
//////////////////////////////////////////////////////////////////

