	str = str.replace(/&#250;/gm, "ú");
	str = str.replace(/&uacute;/gm, "ú");
	str = str.replace(/&#220;/gm, "Ü");
	str = str.replace(/&Uuml;/gm, "Ü");
	str = str.replace(/&#252;/gm, "ü");
	str = str.replace(/&uuml;/gm, "ü");
	str = str.replace(/&#171;/gm, "«");
	str = str.replace(/&laquo;/gm, "«");
	str = str.replace(/&#187;/gm, "»");
	str = str.replace(/&raquo;/gm, "»");
	str = str.replace(/&#191;/gm, "¿");
	str = str.replace(/&iquest;/gm, "¿");
	str = str.replace(/&#161;/gm, "¡");
	str = str.replace(/&iexcl;/gm, "¡");
	str = str.replace(/&#128;/gm, "€");
	str = str.replace(/&euro;/gm, "€");
	str = str.replace(/&#8359;/gm, "\u20A7");

	return str;
}

function convertTurkishHTMLCode(str) {
	str = str.replace(/&#304;/gm, "İ");
	str = str.replace(/&#305;/gm, "ı");
	str = str.replace(/&#214;/gm, "Ö");
	str = str.replace(/&#246;/gm, "ö");
	str = str.replace(/&#220;/gm, "Ü");
	str = str.replace(/&#252;/gm, "ü");
	str = str.replace(/&#199;/gm, "Ç");
	str = str.replace(/&#231;/gm, "ç");
	str = str.replace(/&#286;/gm, "Ğ");
	str = str.replace(/&#287;/gm, "ğ");
	str = str.replace(/&#350;/gm, "Ş");
	str = str.replace(/&#351;/gm, "ş");
	str = str.replace(/&#8356;/gm, "₤");

	return str;
}
