record.script:
//////////////////////////////////////////////////////////////////
//Create scmessage Record Start.
//////////////////////////////////////////////////////////////////
function addMessageRecord(msgclass, tag, text, language )
{
	var message = new SCFile("scmessage");
	var sql = "syslanguage=\"" + language +"\" and class=\""+ msgclass + "\" and message.id=\""+ tag +"\""; 

	if ( message.doSelect( sql ) != RC_SUCCESS )
	{
		message.syslanguage = language;
		message._class = msgclass;
		message.message_id = tag;
		message.message = text;		
		message.doInsert();
	}
}
//////////////////////////////////////////////////////////////////
//Create scmessage Record End.
//////////////////////////////////////////////////////////////////

/////////////////////////////////////////////////////////////////
//Covert Special String Start.
//////////////////////////////////////////////////////////////////
function strCovert(str)
{
  str = str.toString();
  str = str.replace("[C++ object Datum] - ", "");
  //str = str.replace(new RegExp("\"", "g"), "\\\"");
  return str;
}
//////////////////////////////////////////////////////////////////
//Covert Special String End.
//////////////////////////////////////////////////////////////////
