record.script:
// Note. DO NOT call this script outside unit test!

var ArrayProto = Array.prototype, ObjProto = Object.prototype, FuncProto = Function.prototype;
var toString = ObjProto.toString;
var _type = funcs.type;

function isJsArray(obj) {
    return toString.call(obj) == '[object Array]';
}

function isScArray(obj) {
    return _type(obj) === 8 && (toString.call(obj) === '[object SCDatum]' || toString.call(obj) === '[object Datum]');
}

function isArray(obj) {
    return isJsArray(obj) || isScArray(obj);
}

function arrayLen(obj) {
    if (isScArray(obj)) {
        return obj.length();
    } else if (isArray(obj) || isString(obj)) {
        return obj.length;
    }      
}

function isString(obj) {
    return toString.call(obj) == '[object String]';
}
