record.script:
/**
 * Get the configuration item of Microsoft Teams Integration
 */
function getConfigurationItem() {
    return lib.smis_ConfigurationManager.getEnabledConfigItem("MicrosoftTeams");
}


function isEnabled() {
    return getConfigurationItem() != null;
}


function getConfigParameterValue(param) {
    var config = getConfigurationItem();
    if (config != null) {
        return config.getConfigParameterValue(param);
    } else {
        return null;
    }
}


function getUsername() {
    return getConfigParameterValue("username");
}


function getPassword() {
    return getConfigParameterValue("password");
}


function getClientID() {
    return getConfigParameterValue("clientID");
}


function getTenantID() {
    return getConfigParameterValue("tenantID");
}


function getClientSecret() {
    return getConfigParameterValue("clientSecret");
}


function getProxyHost() {
    return getConfigParameterValue("proxyHost");
}


function getProxyPort() {
    return getConfigParameterValue("proxyPort");
}


function getProxyUsername() {
    return getConfigParameterValue("proxyUsername");
}


function getProxyPassword() {
    return getConfigParameterValue("proxyPassword");
}


function getMessagePostTemplate() {
    return getConfigParameterValue("messagePostTemplate");
}


function isSynchronizeMessage() {
    return getConfigParameterValue("synchronizeMessage");
}
