function doGetAction4Config(actionURLs, actionParam)
{
  var objRtn = "";
  var actionName = actionURLs[1];

  switch (actionName) {
    case "sxTick":
      objRtn = GetSXTickConfig();
      break;
    default:
      objRtn = {};	
  }

  return objRtn;
}

