var JSON = lib.JSON.json();

function doGetAction4Notification(actionURLs, actionParam)
{
  var objRtn = "";
  var actionName = actionURLs[1];

  switch (actionName) {
    case "count":
      objRtn = GetNotificationCount(actionURLs, actionParam);
      break;
    default:
      objRtn = {};	
  }

  return objRtn;
}

function GetNotificationCount(actionURLs, actionParam)
{
  var objRtn =  {"read":1,"unread":22,"total":23};

  return objRtn;
}
