record.script:
function enableSecurity() {
    var ret = new SCDatum();
    var triggerStatus = funcs.sysinfo_get("trigger");
    funcs.rtecall("trigger", ret, 1);
    lib.ConfigurationUpgradeManager.getInstance().upgradeProfiles();
    funcs.rtecall("trigger", ret, 0);
    lib.ConfigurationUpgradeManager.getInstance().upgradeOperators();
    funcs.rtecall("trigger", ret, triggerStatus);
    lib.ConfigurationUpgradeManager.getInstance().upgradeUserRoles();
}
