          if(area[iCounter]==null)
           area[iCounter]=system.functions.scmsg("69","ruleset");
          else 
           area[iCounter]=area[iCounter]+", "+system.functions.scmsg("69","ruleset");
        } 

       }
       for(j=0;j<wfPhase.afterUpdateRules.length();j++)
