//Generate Impact, Urgency, and Priority readable label
var _priority = $RECORD.priority_code;
var _severity = $RECORD.severity;
var _impact = $RECORD.initial_impact;
var _risk = $RECORD.risk_assessment;

var priority = lib.htmlemailtemplates.getPriorityString(_priority);
var severity = lib.htmlemailtemplates.getSeverityString(_severity);
var impact   = lib.htmlemailtemplates.getImpactString(_impact);
var risk     = lib.htmlemailtemplates.getRiskString(_risk);


var operator = $RECORD.operator;
var old_start = $RECORDOLD.down_start;
var old_end = $RECORDOLD.down_end;
var new_start = $RECORD.down_start;
var new_end = $RECORD.down_end;
var label = "";
if(old_start && !new_start)  label= "The outage for "+ $RECORD.logical_name  + " has been cancelled.";
if(!old_start && new_start && new_end)  label= $RECORD.logical_name + " is scheduled to be unavailable from " + new_start + " to " + new_end + ".";
if(!old_start && new_start && !new_end)  label= $RECORD.logical_name + " is scheduled to be unavailable from " + new_start + ".";
if(old_start && new_start && new_end && old_end)   label= "The outage for "+ $RECORD.logical_name + " originally planned for " + old_start + " - " + old_end + " has been rescheduled to " + new_start + " - " + new_end +".";
if(old_start && new_start && new_end && !old_end)  label= "The outage for "+ $RECORD.logical_name + " originally planned for " + old_start + " has been rescheduled to " + new_start + " - " + new_end +".";
if(old_start && new_start && !new_end && old_end)  label= "The outage for "+ $RECORD.logical_name + " originally planned for " + old_start + " - " + old_end + " has been rescheduled to " + new_start + ".";
if(old_start && new_start && !new_end && !old_end) label= "The outage for "+ $RECORD.logical_name + " originally planned for " + old_start + " has been rescheduled to " + new_start + ".";
var email_title="";
var email_body_title="";
email_title=email_body_title=label;

