function getContactName( loginName )
{
  var q = 'SELECT contact.name FROM operator WHERE name ="' + loginName + '"';
  var f = new SCFile("operator");
  var rc = f.doSelect(q);

  var ret = loginName;
  if ( rc == RC_SUCCESS )
  {
    ret = f.contact_name;
  }
  delete f;

  return ret;
}
