vars.$query = lib.BSGLink.getBsgLinkQuery('logical.name');

var MAX = 500;
if (vars.$File.affected_item != null && vars.$File.contact_name != null) {
    var count = 0;
    var list = [];
    lib.c.$('device').select(vars.$query).iterate(function(item){
        list.push(item['logical.name']);

        if(++count>=MAX) {
            vars.$fill_skip = true;
            vars.$find_skip = true;
            print(lib.rtecallUtil.scmsg(154,"icm"));
            return true;
        }
    });

    vars.$memberList = list;
    vars.$query = "logical.name isin $memberList or contact.name=\"" + lib.StringUtil.escapeAllStr(vars.$File.contact_name) + "\"";
}
