record.javascript:
var length = record.attrs.length();

for(var i = 0; i < length;i++){
	if(null==record.attrs[i].fileextension && null==record.attrs[i].description){
		var fileextension = record.attrs[i].fileextension;
		var description = record.attrs[i].description;

		for(var j = i;j<length;j++){
			record.attrs[j].fileextension = record.attrs[j+1].fileextension;
			record.attrs[j].description = record.attrs[j+1].description;
		}
		record.attrs[length-1].fileextension = fileextension;
		record.attrs[length-1].description = description;
	}
}
