record.javascript:
if ((record.type == "category" || oldrecord.type == "category") && (oldrecord.name != record.name || oldrecord.displayName != record.displayName || oldrecord.non_cart != record.non_cart))
{
	if (record.non_cart || oldrecord.non_cart)
	{		
		system.library.svcCatalogTriggers.refreshCategoriesGL(true);
	}
	if (!(record.non_cart && oldrecord.non_cart))
	{
		system.library.svcCatalogTriggers.refreshCategoriesGL(false);
	}
}
