record["module.enabled"] = false;
var adapter = record["SMAdapter"];
if (lib[adapter] != null && lib[adapter]["getModuleList"] != null) {
    var moduleList = lib[adapter]["getModuleList"]();
    if (moduleList["valueList"] != null && moduleList["valueList"].length>0) {
        record["module.enabled"] = true;
    }
}
