if(record['default.bot']){
    var sql="number~=\""+record['number']+"\"";
    var bot=new SCFile("sdchatbot");
    if(bot.doSelect(sql)===RC_SUCCESS){
        do{
            bot['default.bot']=false;
            bot.doUpdate();
        }while (bot.getNext()===RC_SUCCESS)
    }
}
