record.javascript.actions:
vars.$DMTTableConfirmedList = [];
vars.$numOfTablesToBeExported=0;

var i, length = funcs.lng(vars.$deltaMigrationToolTableName);
for(i=0;i<length;i++){
	var curItem="";
	curItem+=vars.$deltaMigrationToolTableName[i]+"$";
	curItem+=vars.$deltaMigrationToolTimestampField[i]+"$";
	curItem+=vars.$deltaMigrationToolSuggestion[i]+"$";
	curItem+=vars.$deltaMigrationToolTimeEstimation[i]+"$";
	if(vars.$deltaMigrationToolDecision[i]=="Yes"){
		curItem+="true$";
		vars.$numOfTablesToBeExported++;
	}
	else {
		curItem+="false$";
	}
	curItem+=vars.$deltaMigrationToolComment[i];
	vars.$DMTTableConfirmedList=funcs.insert(vars.$DMTTableConfirmedList,0,1,curItem);
}
if(vars.$numOfTablesToBeExported==0){
	lib.DeltaMigrationUtil.messageList_add("error","Not find delta data.");
}

record.javascript.init:
vars.$htmlDescpForWizard = lib.DeltaMigrationUtil.getDescriptionHtmlForWizards(vars.$L_wizard.name);

vars.$DMTTableCheckList = lib.DeltaMigrationExport.unload_data_pre_check(vars.$unlFilePathName, vars.$startTime,vars.$endTime);

vars.$deltaMigrationToolTableName=[];
vars.$deltaMigrationToolTimestampField=[];
vars.$deltaMigrationToolSuggestion=[];
vars.$deltaMigrationToolTimeEstimation=[];
vars.$deltaMigrationToolDecision=[];
vars.$deltaMigrationToolComment=[];

var i, length = lib.ArrayUtil.length(vars.$DMTTableCheckList);
for (i = 0; i < length; i++) {
	deltaMigrationTool_tableCheck=""+vars.$DMTTableCheckList[i];
	var row = deltaMigrationTool_tableCheck.split("$");
	vars.$deltaMigrationToolTableName.push(funcs.nullsub(row[0],""));
	vars.$deltaMigrationToolTimestampField.push(funcs.nullsub(row[1],""));
	vars.$deltaMigrationToolSuggestion.push(funcs.nullsub(row[2],""));
	vars.$deltaMigrationToolTimeEstimation.push(funcs.nullsub(row[3],""));
	vars.$deltaMigrationToolComment.push(funcs.nullsub(row[5],""));

	if (row[4]=="true" || row[4]==true) {
		vars.$deltaMigrationToolDecision.push("Yes");
	}
	else {
		vars.$deltaMigrationToolDecision.push("No");
	}
}
