record.javascript.actions:
vars.$DMTTableConfirmedList = [];
vars.$numOfTablesToBeImported=0;

var i, length = funcs.lng(vars.$deltaMigrationToolTableName); 
for(i=0;i<length;i++){
	var curItem="";
	curItem+=vars.$deltaMigrationToolTableName[i]+"$";
	curItem+=vars.$deltaMigrationToolSuggestion[i]+"$";
	if(vars.$deltaMigrationToolDecision[i]=="Yes"){
		curItem+="true$";
		vars.$numOfTablesToBeImported++;
	}
	else {
		curItem+="false$";
	}
	curItem+=vars.$deltaMigrationToolComment[i];
	//vars.DMTTableConfirmedList.push(curItem);
	//vars.DMTTableConfirmedList[i]=curItem;
	vars.$DMTTableConfirmedList=funcs.insert(vars.$DMTTableConfirmedList,0,1,curItem);
	//print(vars.DMTTableCheckList);
}
if(vars.numOfTablesToBeImported==0){
	//print("Not find data to import.");
	lib.DeltaMigrationUtil.messageList_add("error","Not find data to import."); 
}
vars.$canShowTableStruct=false;

record.javascript.init:
vars.$htmlDescpForWizard = lib.DeltaMigrationUtil.getDescriptionHtmlForWizards(vars.$L_wizard.name);

//vars.$unlFilePathName="C:\\temp\\DATA_20131211b\\";
vars.$DMTTableCheckList = lib.DeltaMigrationImport.load_data_pre_check(vars.$unlFilePathName);

vars.$deltaMigrationToolTableName = [];
vars.$deltaMigrationToolSuggestion = [];
vars.$deltaMigrationToolDecision = [];
vars.$deltaMigrationToolComment = [];
vars.$deltaMigrationToolStructChangedTables="";
vars.$deltaMigrationToolStructChangedTableArray = [];

var i, length = lib.ArrayUtil.length(vars.$DMTTableCheckList);
for (i = 0; i < length; i++) {
	deltaMigrationTool_tableCheck=""+vars.$DMTTableCheckList[i];
	vars.$deltaMigrationToolTableName.push(funcs.nullsub(deltaMigrationTool_tableCheck.split("$")[0],""));
	vars.$deltaMigrationToolSuggestion.push(funcs.nullsub(deltaMigrationTool_tableCheck.split("$")[1],""));
	vars.$deltaMigrationToolComment.push(funcs.nullsub(deltaMigrationTool_tableCheck.split("$")[3],""));

	if(deltaMigrationTool_tableCheck.split("$")[2]=="true" || deltaMigrationTool_tableCheck.split("$")[2]==true) {
		vars.$deltaMigrationToolDecision.push("Yes");
	}
	else {
		vars.$deltaMigrationToolDecision.push("No");
	}

	if(deltaMigrationTool_tableCheck.split("$")[1]=="need confirm, table structure changed.") {
		vars.$deltaMigrationToolStructChangedTables += ";"+funcs.nullsub(deltaMigrationTool_tableCheck.split("$")[0],"");
	}	
}

if(vars.$deltaMigrationToolStructChangedTables!="") {
	vars.$deltaMigrationToolStructChangedTables=funcs.substr(vars.$deltaMigrationToolStructChangedTables,2);
	vars.$deltaMigrationToolStructChangedTableArray=vars.$deltaMigrationToolStructChangedTables.split(";");
}

vars.$dmtCurTableName="";
