record.javascript.actions:
var xml= new XML();

xml.setContent("<sendHTMLEmail></sendHTMLEmail>");

var tempName = xml.addElement( "tempName");
tempName.setValue( vars.$tempName );

var recipient = xml.addElement( "recipient");
recipient.setValue( vars.$recipient );

var js = xml.addElement( "js");
var userList = xml.addElement( "userList");
var group = xml.addElement( "group");
var field = xml.addElement( "field");

if(vars.$recipient=="Set Using Javascript")
{
 js.setValue(vars.$js);
}		

if(vars.$recipient=="Users")
{
 userList.addAttribute("sendTo", vars.$sendToUsers);
 if(vars.$sendToUsers=="operator")
 {
  vars.$users1=system.functions.denull(vars.$users1);
  var i,length=vars.$users1.length(); 
  for (i=0;i<length;i++)
  {
	var user = userList.addElement( "user" );
	user.setValue( vars.$users1[i] );
  }
 }
 if(vars.$sendToUsers=="contacts")
 {
  vars.$users2=system.functions.denull(vars.$users2);
  var i,length=vars.$users2.length(); 
  for (i=0;i<length;i++)
  {
	var user = userList.addElement( "user" );
	user.setValue( vars.$users2[i] );
  }
 }

}		

if(vars.$recipient=="Group")
{
  group.addAttribute("type",vars.$sendToGroup);
  if(vars.$sendToGroup=="assignment")
  {
   group.addAttribute("sendTo",vars.$gARecp);
  }
  if(vars.$sendToGroup=="kmgroup")
  {
   group.addAttribute("sendTo",vars.$gKRecp);
  }
  if(vars.$sendToGroup=="cm3groups" || vars.$sendToGroup=="ocmgroups")
  { 
   group.addAttribute("sendTo",vars.$gCRRecp);
  }

  if(vars.$sendToGroup=="assignment") 
  {
  	group.setValue(vars.$asgroup);
  }
  if(vars.$sendToGroup=="cm3groups") 
  {
  	group.setValue(vars.$cmgroup);
  }
  if(vars.$sendToGroup=="ocmgroups") 
  {
  	group.setValue(vars.$ocmgroup);
  }
  if(vars.$sendToGroup=="kmgroup") 
  {
  	group.setValue(vars.$kmgroup);
  }
}		

if(vars.$recipient=="Current Record")
{
  field.setValue(vars.$field);
  field.addAttribute("type",vars.$fieldType);
  if(vars.$fieldType=="cm3groups" ||  vars.$fieldType=="ocmgroups")
  {
    field.addAttribute("sendTo",vars.$gCRRecp);
  }
  if(vars.$fieldType=="assignment")
  {
    field.addAttribute("sendTo",vars.$gARecp);
  }  
  if(vars.$fieldType=="kmgroup")
  {
    field.addAttribute("sendTo",vars.$gKRecp);
  }  
}		

vars.$L_file.ruleXML = xml.toXMLString();

record.javascript.init:
var xmlString;
xmlString = vars.$L_file.ruleXML;

if ( xmlString == null || xmlString =="" )
	{
		xmlString="<sendHTMLEmail></tempName><recipient></js><userList></users></userList></group></field></sendHTMLEmail>";
	}


var xml = new XML();
xml.setContent( xmlString);

var tempName = lib.xmlHelpers.getElement( xml, "tempName", null );	
var recipient = lib.xmlHelpers.getElement( xml, "recipient", null );
var js = lib.xmlHelpers.getElement( xml, "js", null );

var userList = lib.xmlHelpers.getElement( xml, "userList", null );
var group = lib.xmlHelpers.getElement( xml, "group", null );
var field = lib.xmlHelpers.getElement( xml, "field", null );


if (tempName != null) 
{ 
 vars.$tempName = tempName.getValue();
 vars.$recipient = recipient.getValue();

 if(vars.$recipient=="Set Using Javascript")
 {
   vars.$js = js.getValue();
 }

 if(vars.$recipient=="Users")
 {  
  vars.$sendToUsers = userList.getAttributeValue("sendTo");
  var checkUser= userList.getFirstChildElement();
  do {         
		if (checkUser != null)
		{
			var user = checkUser.getValue();
			if (user != null && user != "" && vars.$sendToUsers=="operator") 
			{   
				vars.$users1= system.functions.insert(vars.$users1,0,1,user);
			}
			if (user != null && user != "" && vars.$sendToUsers=="contacts") 
			{   
				vars.$users2= system.functions.insert(vars.$users2,0,1,user);
			}	
		}		
		checkUser = checkUser.getNextSiblingElement();
	}	
	while(checkUser != null); 
 } 

 if(vars.$recipient=="Group")
 {
  vars.$sendToGroup = group.getAttributeValue("type");
  if(vars.$sendToGroup=="assignment") 
  {
  	vars.$asgroup= group.getValue();
  }
  if(vars.$sendToGroup=="cm3groups") 
  {
  	vars.$cmgroup= group.getValue();
  }
  if(vars.$sendToGroup=="ocmgroups") 
  {
  	vars.$ocmgroup= group.getValue();
  }
  if(vars.$sendToGroup=="kmgroup") 
  {
  	vars.$kmgroup= group.getValue();
  }
  if(vars.$sendToGroup=="assignment") 
  {
  	vars.$gARecp=group.getAttributeValue("sendTo");
  }
  if(vars.$sendToGroup=="kmgroup") 
  {
  	vars.$gKRecp=group.getAttributeValue("sendTo");
  }
  if(vars.$sendToGroup=="cm3groups" || vars.$sendToGroup=="ocmgroups") 
  {
  	vars.$gCRRecp=group.getAttributeValue("sendTo");
  }
 }

 if(vars.$recipient=="Current Record")  
 {
  vars.$field= field.getValue();
  vars.$fieldType=field.getAttributeValue("type");
  if(vars.$fieldType=="cm3groups" ||  vars.$fieldType=="ocmgroups")
  {
   vars.$gCRRecp=field.getAttributeValue("sendTo");
  }
  if(vars.$fieldType=="assignment")
  {
   vars.$gARecp=field.getAttributeValue("sendTo");
  } 
  if(vars.$fieldType=="kmgroup")
  {
   vars.$gKRecp=field.getAttributeValue("sendTo");
  } 
 } 
}

var datadict = new SCFile("datadict", SCFILE_READONLY);
var sql = "name=\""+system.vars.$L_tablename+"\"";
var rc = datadict.doSelect(sql);
if (rc == RC_SUCCESS)
{
	vars.$fieldNames = datadict.fields;
	vars.$fieldCaptions = datadict.captions;
}
