record.javascript.init:
var xmlString;
xmlString = vars.$L_file.ruleXML;

if(xmlString == null || xmlString =="") {
	xmlString="<setClock></clockAction></clockNameType></clockName></clockScheduleType></clockSchedule></clockTimezoneType></clockTimezone></scriptField></setClock>";
}

var xml = new XML();
xml.setContent(xmlString);
var clockAction = lib.xmlHelpers.getElement(xml, "clockAction", null);
var clockName = lib.xmlHelpers.getElement(xml, "clockName", null);
var clockNameType = lib.xmlHelpers.getElement(xml, "clockNameType", null);
var clockSchedule = lib.xmlHelpers.getElement(xml, "clockSchedule", null);
var clockScheduleType = lib.xmlHelpers.getElement(xml, "clockScheduleType", null);
var clockTimezone = lib.xmlHelpers.getElement(xml, "clockTimezone", null );
var clockTimezoneType = lib.xmlHelpers.getElement( xml, "clockTimezoneType", null);
var scriptField=lib.xmlHelpers.getElement(xml, "scriptField", null);

if (clockAction != null) {
    vars.$clockAction = clockAction.getValue();
    vars.$clockName = clockName.getValue();
    vars.$clockNameType = clockNameType.getValue();
    vars.$scriptField = scriptField.getValue();
    // clock scheudle and clock timezone are new added, to keep the backward compatibility we need to check whether the related xml elements are null or not.
    if(null != clockScheduleType) {
        vars.$clockScheduleType = clockScheduleType.getValue();
    } else {
        vars.$clockScheduleType = "value";
    }
    if(null != clockSchedule) {
        vars.$clockSchedule = clockSchedule.getValue();
    }
    if(null != clockTimezoneType) {
        vars.$clockTimezoneType = clockTimezoneType.getValue();
    } else {
        vars.$clockTimezoneType = "server";
    }
    if(null != clockTimezone) {
        vars.$clockTimezone = clockTimezone.getValue();
    }
} else {
    vars.$clockAction = "startClock";
    vars.$clockNameType = "Fixed Name";
    vars.$clockScheduleType = "value";
    //vars.$clockSchedule = "";
    vars.$clockTimezoneType = "server";
    //vars.$clockTimezone = "";
}

lib.RuleWizardHelpers.populateClockNames();
