record.javascript.actions:
function gerateLoadSchedule(unloadFile, processor) {
    var f = new SCFile("schedule");

    f.application = "bgload";
    f.strings[1] = "database load/unload.winnt";
    f.strings[2] = "false";
    f.expiration = new Date();
    f.strings[0] = unloadFile;
    f.name = "load data - " + unloadFile;
    f._class = processor;
    print("Generate Load Schedule:==>" + f.name);
    f.doInsert();
}

function gerateTriggerOffSchedule(unloadFile, processor) {
    var f = new SCFile("schedule");

    f.javascript = "var ret;\nfuncs.rtecall(\"trigger\", ret, 0);";
    var now = new Date();
    f.expiration = new Date(now.getTime() - 1000);
    f.name = "load data - " + unloadFile + " Trigger Off";
    f._class = processor;
    print("Generate Load Schedule:==>" + f.name);
    f.doInsert();
}

function gerateTriggerOnSchedule(unloadFile, processor) {
    var f = new SCFile("schedule");

    f.javascript = "var ret;\nfuncs.rtecall(\"trigger\", ret, 1);";
    var now = new Date();
    f.expiration = new Date(now.getTime() + 1000);
    f.name = "load data - " + unloadFile + " Trigger On";
    f._class = processor;
    print("Generate Load Schedule:==>" + f.name);
    f.doInsert();
}

if (vars['$unload.schedule.continue']) {
    var unload = vars['$L.file'];

    var unloadFile = vars['$unload.schedule.file'];
    var unloadClass = vars['$unload.schedule.class'];
    var triggeroff = vars['$unload.schedule.triggeroff'];
//    print(triggeroff);
    var listLoad = vars['$unload.schedule.importlist'];
//    print("LL: "+listLoad);

    if (unloadFile && unloadClass) {
        if (triggeroff==true||triggeroff=="true") {
        	gerateTriggerOffSchedule(unloadFile, unloadClass);
        }

        if (listLoad) {
            var listDesc = readFile(unloadFile, "t");
            var list = listDesc.split("\n");
            for (var i = 0; i < list.length; ++i) {
                if (list[i] && list[i] != "") {
                    gerateLoadSchedule(list[i], unloadClass);
                }
            }
        } else {
            gerateLoadSchedule(unloadFile, unloadClass);
        }

        if (triggeroff==true||triggeroff=="true") {
        	gerateTriggerOnSchedule(unloadFile, unloadClass);
        }
    } else {
        print("Please Specify unload file and unload schedule class!");
    }
}
